  const ctx = document.getElementById('fundChart').getContext('2d');
  const fundChart = new Chart(ctx, {
    type: 'line',
    data: {
      labels: [...Array(36).keys()].map(i => `Jul '${22 + Math.floor(i / 12)}`), // Dummy x-axis
      datasets: [{
        label: 'Performance (%)',
        data: [75, 73, 78, 81, 87, 85, 90, 94, 102, 108, 113, 120, 130, 125, 140, 138, 145, 150, 160, 165, 170, 180, 190, 205, 220, 230, 245, 260, 280, 290, 300, 315, 330, 340, 310, 290],
        fill: true,
        borderColor: '#007bff',
        tension: 0.3
      }]
    },
    options: {
      responsive: true,
      plugins: {
        legend: {
          display: false
        }
      },
      scales: {
        y: {
          title: {
            display: true,
            text: 'Performance (%)'
          }
        }
      }
    }
  });
});
